      this.currentStepIndex = 0;
      setTimeout(() => this.executeStep(element), 1000);
    } else {
      this.stop();
      this.onAnimationEnd.next();
    }
  }

  /**
   * Stop the current animation
   */
  stop(): void {
    this.isPlaying = false;
    this.currentStepIndex = 0;
    this.stopFpsMonitoring();
  }

  /**
   * Pause the current animation
   */
  pause(): void {
    this.isPlaying = false;
  }

  /**
   * Resume the paused animation
   */
  resume(element: Element): void {
    if (this.currentSequence && !this.isPlaying) {
      this.isPlaying = true;
      this.executeStep(element);
    }
  }

  /**
   * Start monitoring FPS
   */
  private startFpsMonitoring(): void {
    this.frameCount = 0;
    this.lastFrameTime = performance.now();
    this.monitorFps();
  }

  /**
   * Monitor FPS during animation
   */
  private monitorFps(): void {
    if (!this.isPlaying) return;

    const currentTime = performance.now();
    this.frameCount++;

    // Calculate FPS every 500ms
    if (currentTime - this.lastFrameTime >= 500) {
      this.fps = Math.round((this.frameCount * 1000) / (currentTime - this.lastFrameTime));
      this.onFpsUpdate.next(this.fps);

      this.frameCount = 0;
      this.lastFrameTime = currentTime;
    }

    requestAnimationFrame(() => this.monitorFps());
  }

  /**
   * Stop monitoring FPS
   */
  private stopFpsMonitoring(): void {
    this.fps = 0;
    this.frameCount = 0;
  }

  /**
   * Get current FPS
   */
  getCurrentFps(): number {
    return this.fps;
  }

  /**
   * Check if animation is playing
   */
  isAnimationPlaying(): boolean {
    return this.isPlaying;
  }
}

import { Injectable } from '@angular/core';
import * as d3 from 'd3';
import { GeoProjection } from 'd3-geo';
import { Subject } from 'rxjs';
import { ZoomConfig, ZoomEvent } from '../models/map.types';

/**
 * GeoZoom Service
 * Handles zoom and pan interactions by updating the projection directly
 * This causes the map to re-render with proper geographic transformations
 */
@Injectable({
  providedIn: 'root',
})
export class GeoZoomService {
  private projection?: GeoProjection;
  private element?: Element;
  private width = 0;
  private height = 0;

  // Projection state
  private currentScale = 1;
  private currentRotation: [number, number, number] = [0, 0, 0];
  private currentCenter: [number, number] = [0, 0];

  // Initial state
  private initialScale = 1;
  private initialRotation: [number, number, number] = [0, 0, 0];
  private initialCenter: [number, number] = [0, 0];

  // Config
  private scaleExtent: [number, number] = [0.5, 20];

  // Mouse state
  private isDragging = false;
  private lastMousePos: [number, number] = [0, 0];

  // Bound event handlers (store references for cleanup)
  private boundMouseDown: (e: MouseEvent) => void;
  private boundMouseMove: (e: MouseEvent) => void;
  private boundMouseUp: () => void;
  private boundWheel: (e: WheelEvent) => void;
  private boundTouchStart: (e: TouchEvent) => void;
  private boundTouchMove: (e: TouchEvent) => void;
  private boundTouchEnd: () => void;

  // Observable for projection changes
  public onProjectionChange = new Subject<void>();

  constructor() {
    // Bind event handlers once
    this.boundMouseDown = this.handleMouseDown.bind(this);
    this.boundMouseMove = this.handleMouseMove.bind(this);
    this.boundMouseUp = this.handleMouseUp.bind(this);
    this.boundWheel = this.handleWheel.bind(this);
    this.boundTouchStart = this.handleTouchStart.bind(this);
    this.boundTouchMove = this.handleTouchMove.bind(this);
    this.boundTouchEnd = this.handleTouchEnd.bind(this);
  }

  /**
   * Initialize interaction handlers on a DOM element
   */
  init(
    element: Element,
    projection: GeoProjection,
    width: number,
    height: number,
    config: ZoomConfig = { scaleExtent: [0.5, 20] }
  ): void {
    this.projection = projection;
    this.element = element;
    this.width = width;
    this.height = height;
    this.scaleExtent = config.scaleExtent;

    // Store initial projection state
    this.initialScale = projection.scale();
    this.currentScale = this.initialScale;

    const center = projection.center?.();
    if (center) {
      this.initialCenter = center as [number, number];
      this.currentCenter = [...this.initialCenter];
    }

    const rotate = projection.rotate?.();
    if (rotate) {
      this.initialRotation = rotate as [number, number, number];
      this.currentRotation = [...this.initialRotation];
    }

    // Add event listeners
    this.setupEventListeners(element);
  }

  /**
   * Setup mouse and wheel event listeners
   */
  private setupEventListeners(element: Element): void {
    const el = element as HTMLElement;

    // Make element focusable for wheel events
    if (!el.hasAttribute('tabindex')) {
      el.setAttribute('tabindex', '0');
    }

    // Mouse drag for panning - mousedown on element
    el.addEventListener('mousedown', this.boundMouseDown);

    // mousemove and mouseup on document for better drag handling
    document.addEventListener('mousemove', this.boundMouseMove);
    document.addEventListener('mouseup', this.boundMouseUp);

    // Wheel for zooming
    el.addEventListener('wheel', this.boundWheel, { passive: false });

    // Touch support
    el.addEventListener('touchstart', this.boundTouchStart, { passive: false });
    el.addEventListener('touchmove', this.boundTouchMove, { passive: false });
    el.addEventListener('touchend', this.boundTouchEnd);
  }

  /**
   * Handle mouse down - start dragging
   */
  private handleMouseDown(event: MouseEvent): void {
    event.preventDefault();
    this.isDragging = true;
    this.lastMousePos = [event.clientX, event.clientY];
    (this.element as HTMLElement).style.cursor = 'grabbing';
  }

  /**
   * Handle mouse move - update projection during drag
   */
  private handleMouseMove(event: MouseEvent): void {
    if (!this.isDragging || !this.projection) return;

    const dx = event.clientX - this.lastMousePos[0];
    const dy = event.clientY - this.lastMousePos[1];
    this.lastMousePos = [event.clientX, event.clientY];

    // Calculate rotation change based on mouse movement
    const sensitivity = 0.5; // Increase sensitivity for better responsiveness
    const rotationX = -dy * sensitivity;
    const rotationY = dx * sensitivity;

    // Update rotation (for equirectangular, this handles panning)
    this.currentRotation = [
      this.currentRotation[0] + rotationY,
      this.currentRotation[1] + rotationX,
      this.currentRotation[2]
    ];

    // Apply rotation to projection
    if (this.projection.rotate) {
      this.projection.rotate(this.currentRotation);
    }

    // Trigger re-render
    this.onProjectionChange.next();
  }

  /**
   * Handle mouse up - stop dragging
   */
  private handleMouseUp(): void {
    this.isDragging = false;
    if (this.element) {
      (this.element as HTMLElement).style.cursor = 'grab';
    }
  }

  /**
   * Handle wheel - zoom in/out
   */
  private handleWheel(event: WheelEvent): void {
    event.preventDefault();
    if (!this.projection) return;

    // Calculate new scale
    const delta = -event.deltaY;
    const scaleFactor = delta > 0 ? 1.1 : 0.9;
    let newScale = this.currentScale * scaleFactor;

    // Clamp to scale extent
    newScale = Math.max(this.scaleExtent[0] * this.initialScale,
                       Math.min(this.scaleExtent[1] * this.initialScale, newScale));

    this.currentScale = newScale;
    this.projection.scale(newScale);

    // Trigger re-render
    this.onProjectionChange.next();
  }

  /**
   * Touch support - start
   */
  private touchStartPos: [number, number] = [0, 0];
  private handleTouchStart(event: TouchEvent): void {
    if (event.touches.length === 1) {
      event.preventDefault();
      this.isDragging = true;
      const touch = event.touches[0];
      this.lastMousePos = [touch.clientX, touch.clientY];
      this.touchStartPos = [touch.clientX, touch.clientY];
    }
  }

  /**
   * Touch support - move
   */
  private handleTouchMove(event: TouchEvent): void {
    if (event.touches.length === 1 && this.isDragging) {
      event.preventDefault();
      const touch = event.touches[0];

      const dx = touch.clientX - this.lastMousePos[0];
      const dy = touch.clientY - this.lastMousePos[1];
      this.lastMousePos = [touch.clientX, touch.clientY];

      if (!this.projection) return;

      const sensitivity = 0.5;
      const rotationX = -dy * sensitivity;
      const rotationY = dx * sensitivity;

      this.currentRotation = [
        this.currentRotation[0] + rotationY,
        this.currentRotation[1] + rotationX,
        this.currentRotation[2]
      ];

      if (this.projection.rotate) {
        this.projection.rotate(this.currentRotation);
      }

      this.onProjectionChange.next();
    }
  }

  /**
   * Touch support - end
   */
  private handleTouchEnd(): void {
    this.isDragging = false;
  }

  /**
   * Programmatically set projection center (via rotation for equirectangular)
   */
  setCenter(center: [number, number], triggerRender = true): void {
    if (!this.projection) return;

    // For equirectangular and similar projections, use rotation
    this.currentRotation = [-center[0], -center[1], 0];

    if (this.projection.rotate) {
      this.projection.rotate(this.currentRotation);
    }

    if (triggerRender) {
      this.onProjectionChange.next();
    }
  }

  /**
   * Programmatically set projection scale
   */
  setScale(scale: number, triggerRender = true): void {
    if (!this.projection) return;

    this.currentScale = this.initialScale * scale;
    this.projection.scale(this.currentScale);

    if (triggerRender) {
      this.onProjectionChange.next();
    }
  }

  /**
   * Animate to a specific center and scale
   */
  animateTo(
    center: [number, number],
    scale: number,
    duration: number = 750,
    callback?: () => void
  ): void {
    if (!this.projection) return;

    const startCenter = [...this.currentCenter];
    const startScale = this.currentScale / this.initialScale;
    const startTime = Date.now();

    const animate = () => {
      const elapsed = Date.now() - startTime;
      const t = Math.min(elapsed / duration, 1);

      // Easing function
      const eased = d3.easeCubicInOut(t);

      // Interpolate center
      const interpCenter: [number, number] = [
        startCenter[0] + (center[0] - startCenter[0]) * eased,
        startCenter[1] + (center[1] - startCenter[1]) * eased
      ];

      // Interpolate scale
      const interpScale = startScale + (scale - startScale) * eased;

      // Update projection
      this.setCenter(interpCenter, false);
      this.setScale(interpScale, false);

      // Trigger render
      this.onProjectionChange.next();

      if (t < 1) {
        requestAnimationFrame(animate);
      } else {
        callback?.();
      }
    };

    requestAnimationFrame(animate);
  }

  /**
   * Reset projection to initial state
   */
  reset(duration: number = 750, callback?: () => void): void {
    this.animateTo(this.initialCenter, 1, duration, callback);
  }

  /**
   * Get current projection state
   */
  getCurrentState() {
    return {
      center: [...this.currentCenter] as [number, number],
      scale: this.currentScale / this.initialScale,
      rotation: [...this.currentRotation] as [number, number, number]
    };
  }

  /**
   * Destroy and cleanup
   */
  destroy(element: Element): void {
    const el = element as HTMLElement;

    // Remove event listeners from element
    el.removeEventListener('mousedown', this.boundMouseDown);
    el.removeEventListener('wheel', this.boundWheel);
    el.removeEventListener('touchstart', this.boundTouchStart);
    el.removeEventListener('touchmove', this.boundTouchMove);
    el.removeEventListener('touchend', this.boundTouchEnd);

    // Remove event listeners from document
    document.removeEventListener('mousemove', this.boundMouseMove);
    document.removeEventListener('mouseup', this.boundMouseUp);

    this.onProjectionChange.complete();
  }
}

import { Injectable } from '@angular/core';
import * as d3 from 'd3';
import { GeoPath, GeoProjection } from 'd3-geo';
import { FeatureCollection } from 'geojson';
import { RenderMode } from '../models/map.types';

export interface RenderContext {
  svg?: d3.Selection<SVGSVGElement, unknown, null, undefined>;
  canvas?: HTMLCanvasElement;
  context?: CanvasRenderingContext2D;
  mode: RenderMode;
  path: GeoPath;
}

@Injectable({
  providedIn: 'root',
})
export class MapRendererService {
  /**
   * Initialize SVG renderer
   */
  initSvgRenderer(
    container: HTMLElement,
    width: number,
    height: number,
    projection: GeoProjection
  ): RenderContext {
    const svg = d3
      .select(container)
      .append('svg')
      .attr('width', width)
      .attr('height', height)
      .attr('class', 'map-svg');

    const path = d3.geoPath().projection(projection);

    return {
      svg,
      mode: 'svg',
      path,
    };
  }

  /**
   * Initialize Canvas renderer (for future implementation)
   */
  initCanvasRenderer(
    container: HTMLElement,
    width: number,
    height: number,
    projection: GeoProjection
  ): RenderContext {
    const canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    canvas.className = 'map-canvas';
    container.appendChild(canvas);

    const context = canvas.getContext('2d');
    if (!context) {
      throw new Error('Could not get 2D context from canvas');
    }

    const path = d3.geoPath().projection(projection).context(context);

    return {
      canvas,
      context,
      mode: 'canvas',
      path,
    };
  }

  /**
   * Render GeoJSON data
   */
  renderGeoJson(renderContext: RenderContext, data: FeatureCollection): void {
    if (renderContext.mode === 'svg' && renderContext.svg) {
      this.renderSvg(renderContext, data);
    } else if (renderContext.mode === 'canvas' && renderContext.context) {
      this.renderCanvas(renderContext, data);
    }
  }

  /**
   * Render to SVG
   */
  private renderSvg(renderContext: RenderContext, data: FeatureCollection): void {
    if (!renderContext.svg) return;

    // Remove existing paths
    renderContext.svg.selectAll('path').remove();

    // Add new paths
    renderContext.svg
      .selectAll('path')
      .data(data.features)
      .join('path')
      .attr('d', renderContext.path)
      .attr('class', 'geo-feature')
      .attr('fill', '#ccc')
      .attr('stroke', '#333')
      .attr('stroke-width', 0.5);
  }

  /**
   * Render to Canvas
   */
  private renderCanvas(renderContext: RenderContext, data: FeatureCollection): void {
    if (!renderContext.context || !renderContext.canvas) return;

    const ctx = renderContext.context;

    // Clear canvas
    ctx.clearRect(0, 0, renderContext.canvas.width, renderContext.canvas.height);

    // Draw features
    ctx.strokeStyle = '#333';
    ctx.lineWidth = 0.5;
    ctx.fillStyle = '#ccc';

    ctx.beginPath();
    renderContext.path(data);
    ctx.fill();
    ctx.stroke();
  }

  /**
   * Clear the rendering context
   */
  clear(renderContext: RenderContext): void {
    if (renderContext.mode === 'svg' && renderContext.svg) {
      renderContext.svg.selectAll('*').remove();
    } else if (renderContext.mode === 'canvas' && renderContext.context && renderContext.canvas) {
      renderContext.context.clearRect(0, 0, renderContext.canvas.width, renderContext.canvas.height);
    }
  }

  /**
   * Update projection for render context
   */
  updateProjection(renderContext: RenderContext, projection: GeoProjection): void {
    renderContext.path = d3.geoPath().projection(projection);
    if (renderContext.mode === 'canvas' && renderContext.context) {
      renderContext.path.context(renderContext.context);
    }
  }
}

